import {useState} from 'react';
import {Dialog,Box,TextField,Typography,Button,styled} from '@mui/material';

const Component =styled(Box)`
height:70vh;
width:90vh;
`;
const Image = styled(Box)`
background:#2874f0 url(https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png)center 85% no-repeat;
height:100%;
width:40%;
padding:45px 35px;
& > p, &> h5{
  color:#FFFFFF;
  font-weight:600;
}

`;
const Wrapper =styled(Box)`
display:flex;
flex-direction:column;
padding:25px 35px;
flex:1;
& > div &,Button, &> p {
  margin-top:20px;
}
`;
const LoginButton = styled(Button)`
text-transform:none;
background:#FB641B;
color: #FFF;
height:48px;
border-radius:2px;
`;
const RequestOTP = styled(Button)`
text-transform:none;
background:#FFF;
color: #2874f0;
height:48px;
border-radius:2px;
Box-shadow:0 2px 4px 0 rgb(0 0 0/20%);
`;
const Text= styled(Typography)`
font-size:12px;
color:#878787;
`;
const CreateAccount = styled(Typography)`
font-size:14px;
text-align:center;
color:#2874f0;
font-weight:600;
cursor:pointer;
`;
const accountIntialValues ={
  login: {
    view:'login'
  },
  signup:{
    view: 'signup'
  },
}
const LoginDialog =({open, setOpen}) => {
  const [account, toggleAccount] =useState(accountIntialValues.login)
  const handleClose =() =>{
    setOpen(false);
  }

    return (
        <Dialog open={open} onClose ={handleClose} PaperProps={{ sx: {maxWidth:'unset'}}} >
          <Component>
            <Box style ={{display:'flex',height:'100%'}}>
            <Image>
              <Typography variant ="h5">Login</Typography>
              <Typography style ={{marginTop:20}}>Get access to your Orders, Wishlist and Recommendations</Typography>
            </Image>
            {
              account.view === 'login'?
            <Wrapper>
              <TextField variant ="standard" label ="Enter Email/Mobile number"/>
              <TextField variant ="standard" label ="Enter Password"/>
              <Text>By continuing, you agree to Flipkart's Terms of use and Privacy Policy.</Text>
              <LoginButton>Login</LoginButton>
              <Typography style={{textAlign:'center'}}>OR</Typography>
              
              <CreateAccount>New to Flipkart an acoount</CreateAccount>
            </Wrapper>
            :
            <Wrapper>
              <TextField variant ="standard" label ="Enter First Name"/>
              <TextField variant ="standard" label ="Enter Last Name"/>
              <TextField variant ="standard" label ="Enter Username"/>
              <TextField variant ="standard" label ="Enter Email"/>
              <TextField variant ="standard" label ="Enter Password"/>
              <TextField variant ="standard" label ="Enter Phone"/>
              <LoginButton>Continue</LoginButton>
              
              <CreateAccount>New to Flipkart an acoount</CreateAccount>
            </Wrapper>
}
            </Box>
          </Component>
        </Dialog>
    )
}
export default LoginDialog;
