    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 1
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      item: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      ite: 1
    }
  };

 
    const data = {
      desktop: <DesktopComponent />,
      tablet: <TabletComponent />,
      mobile: <MobileComponent />,
    };
    const Banner = () => {
    return (
      <Carousel>
      <div>
        {data.desktop}
        {data.tablet}
        {data.mobile}
      </div>
    );
  };
        </Carousel>    )
}
export default Banner;
